function createModal(){$("#create-modal").modal("show")}function editModal(a){axios.get($("#page-axios-data").data("edit"),{params:{id:a}}).then(a=>{$("#update_name").val(a.data.data.name),$("#update_id").val(a.data.data.id),$("#edit-modal").modal("show")}).catch(a=>{showAxiosErrors(a)})}function store(a){var t=a.serialize();axios.post($("#page-axios-data").data("create"),t).then(function(t){toastr.success(t.data.message,"Success"),$("#create-modal").modal("hide"),a.trigger("reset"),$($("#page-axios-data").data("table-id")).DataTable().ajax.reload(null,!1)}).catch(a=>{showAxiosErrors(a)})}function update(a){var t=a.serialize();axios.put($("#page-axios-data").data("update"),t).then(function(t){toastr.success(t.data.message,"Success"),$("#edit-modal").modal("hide"),a.trigger("reset"),$($("#page-axios-data").data("table-id")).DataTable().ajax.reload(null,!1)}).catch(a=>{showAxiosErrors(a)})}$(document).ready(function(){$("#create-form").submit(function(a){a.preventDefault(),store($(this))}),$("#update-form").submit(function(a){a.preventDefault(),update($(this))})});